// Generate unique TraceID and validate input
const timestamp = Date.now();
const randomId = Math.random().toString(36).substring(7);
const traceId = `wf0-${timestamp}-${randomId}`;

// Get input from webhook body
const webhookData = $input.first().json.body || $input.first().json;
const sourceType = webhookData.sourceType || 'jira';
const sourceId = webhookData.sourceId || '';

if (!sourceId) {
  throw new Error('sourceId is required. Please provide a Jira issue key');
}

// Validate sourceType
if (!['jira', 'confluence'].includes(sourceType)) {
  throw new Error('sourceType must be either "jira" or "confluence"');
}

console.log(`Starting WF-0 with TraceID: ${traceId}`);
console.log(`Source: ${sourceType} - ${sourceId}`);

return {
  traceId,
  sourceType,
  sourceId,
  startTime: new Date().toISOString(),
  workflowVersion: '2.1.0-webhook',
  webhookHeaders: $input.first().json.headers || {}
};

// Merge source content and existing epics
const sourceContent = $('Fetch Source Content').first().json;
const existingEpics = $('Fetch Existing Epics').all() || [];
const traceId = $input.all()[0].json.traceId;

// Check if source fetch failed
if (sourceContent.error) {
  throw new Error(`Failed to fetch source: ${sourceContent.error}`);
}

const contextPackage = {
  traceId,
  sourceContent: {
    key: sourceContent.key,
    summary: sourceContent.fields?.summary || 'No summary',
    description: sourceContent.fields?.description || '',
    issueType: sourceContent.fields?.issuetype?.name || 'Unknown'
  },
  existingEpics: Array.isArray(existingEpics) ? existingEpics.map(epic => ({
    key: epic.json.key,
    summary: epic.json.fields?.summary,
    description: epic.json.fields?.description
  })) : []
};

return contextPackage;

