// Parse duplicate check response
const rawText = $input.first().json.output;
const context = $input.first().json;

if (!rawText || typeof rawText !== 'string') {
  return { isDuplicate: false, similarity_score: 0, confidence_score: 0 };
}

const startIndex = rawText.indexOf('{');
const endIndex = rawText.lastIndexOf('}');

if (startIndex !== -1 && endIndex !== -1) {
  try {
    const parsed = JSON.parse(rawText.substring(startIndex, endIndex + 1));
    return {
      ...parsed,
      traceId: context.traceId,
      sourceContent: context.sourceContent
    };
  } catch (error) {
    console.log('Parse error:', error);
    return { isDuplicate: false, similarity_score: 0 };
  }
}
return { isDuplicate: false, similarity_score: 0 };

// HITL notification for duplicate
const data = $input.first().json;

return {
  status: 'HITL_REQUIRED',
  reason: 'DUPLICATE_DETECTED',
  traceId: data.traceId,
  message: `Duplicate detected with ${data.most_similar_epic}. Similarity: ${data.similarity_score}`,
  action_required: 'Review and confirm if this should proceed as new work'
};

// Prepare context for combined planning agent
const data = $('Prepare Duplicate Context').first().json;

const planningContext = {
  traceId: data.traceId,
  requirement: {
    key: data.sourceContent.key,
    title: data.sourceContent.summary,
    description: data.sourceContent.description,
    type: data.sourceContent.issueType
  },
  timestamp: new Date().toISOString()
};

console.log('Preparing for combined planning agent...');
return planningContext;

// Parse combined planning output
const rawText = $input.first().json.output;
const context = $('Prepare Planning Context').first().json;

if (!rawText || typeof rawText !== 'string') {
  throw new Error('Invalid planning agent response');
}

const startIndex = rawText.indexOf('{');
const endIndex = rawText.lastIndexOf('}');

if (startIndex === -1 || endIndex === -1) {
  throw new Error('No JSON found in planning response');
}

try {
  const jsonString = rawText.substring(startIndex, endIndex + 1);
  const parsed = JSON.parse(jsonString);

  if (!parsed.epics || !Array.isArray(parsed.epics)) {
    throw new Error('Invalid planning output: missing epics');
  }

  return {
    ...parsed,
    traceId: context.traceId,
    sourceKey: context.requirement.key,
    processingTime: new Date().toISOString()
  };
} catch (error) {
  console.log('Failed to parse planning output:', error);
  throw new Error(`Planning parse error: ${error}`);
}

// HITL for low confidence
const data = $input.first().json;

return {
  status: 'HITL_REQUIRED',
  reason: 'LOW_CONFIDENCE',
  traceId: data.traceId,
  confidence_score: data.confidence_score,
  message: 'Planning confidence below threshold. Human review required.',
  planning_output: data,
  questions: data.questions || []
};

// Process epics for batch Jira creation
const planningData = $input.first().json;
const epicsToCreate = [];

// Store planning data for later use
const contextData = {
  traceId: $('Generate TraceID').first().json.traceId,
  sourceKey: planningData.sourceKey,
  planningSummary: planningData.planning_summary,
  allEpics: planningData.epics
};

// Prepare each epic for creation
for (const epic of planningData.epics) {
  epicsToCreate.push({
    summary: epic.summary,
    description: epic.description,
    labels: ['ai-generated', 'wf-0', `trace-${contextData.traceId}`],
    priority: epic.priority || 'Medium',
    businessValue: epic.business_value,
    successCriteria: epic.success_criteria.join('\n'),
    // Attach full epic data for story creation later
    _epicData: epic,
    _context: contextData
  });
}

console.log(`Prepared ${epicsToCreate.length} epics for creation`);
return epicsToCreate;

// Store created epic data with original planning
const createdEpic = $input.first().json;
const originalData = $input.first().json._epicData;
const context = $input.first().json._context;

return {
  epicKey: createdEpic.key,
  labels: createdEpic.labels,
  priority: createdEpic.priority,
  epicId: createdEpic.id,
  epicSummary: createdEpic.summary,
  originalEpicData: originalData,
  context: context
};

// Aggregate all created epics
const items = $input.all();
const createdEpics = [];
const allStories = [];

// Process each created epic
for (const item of items) {
  const epicData = item.json;
  createdEpics.push({
    key: epicData.key,
    id: epicData.id,
    summary: epicData.epicSummary
  });

  // Prepare user stories for this epic
  if (epicData.originalEpicData.user_stories) {
    for (const story of epicData.originalEpicData.user_stories) {
      allStories.push({
        epicId: epicData.id,
        epicKey: epicData.key,
        type: 'user',
        summary: story.summary,
        description: story.description,
        acceptanceCriteria: story.acceptance_criteria.join('\n'),
        storyPoints: story.story_points,
        priority: story.priority || 'Medium',
        labels: ['user-story', 'ai-generated', `epic-${epicData.key}`]
      });
    }
  }

  // Prepare technical stories for this epic
  if (epicData.originalEpicData.technical_stories) {
    for (const techStory of epicData.originalEpicData.technical_stories) {
      const subtasks = techStory.sub_tasks
        .map(t => `- ${t.title} (${t.estimated_hours}h)`)
        .join('\n');

      allStories.push({
        epicId: epicData.id,
        epicKey: epicData.key,
        type: 'technical',
        summary: `[Tech] ${techStory.summary}`,
        description: `${techStory.description}\n\n**Category:** ${techStory.category}\n**Risk Level:** ${techStory.risk_level}\n\n**Sub-tasks:**\n${subtasks}`,
        category: techStory.category,
        riskLevel: techStory.risk_level,
        labels: ['technical-story', techStory.category, 'ai-generated', `epic-${epicData.key}`]
      });
    }
  }
}

// Return aggregated data
return {
  summary: {
    epicsCreated: createdEpics.length,
    totalStories: allStories.length,
    epics: createdEpics
  },
  stories: allStories,
  context: items[0].json.context
};

// Get all stories to create
const data = $input.first().json;
const stories = data.stories;

if (!stories || stories.length === 0) {
  return {
    ...data,
    storiesCreated: []
  };
}

// Return each story as separate item for batch processing
return stories;

// Aggregate all created stories
const items = $input.all();

return {
  storiesCreated: items,
  totalStories: items.length
};

// Prepare final response for webhook
const result = $input.first().json;
const timestamp = new Date().toISOString();

const response = {
  status: 'SUCCESS',
  traceId: result.traceId,
  message: 'WF-0 Project Initiation completed successfully',
  timestamp: timestamp,
  summary: {
    epicsCreated: $('Aggregate Epics & Prepare Stories').first().json.summary.epicsCreated,
    storiesCreated: result.totalStories,
    //epicsCreated: result.summary.epicsCreated,
    //storiesCreated: result.summary.storiesCreated,
    //documentsGenerated: result.summary.documentsGenerated
  },
  createdItems: {
    epics: $('Aggregate Epics & Prepare Stories').first().json.summary.epics.map(e => ({
      key: e.key,
      summary: e.summary,
      url: `https://tekcent.atlassian.net/browse/${e.key}`
    })),
    stories: result.storiesCreated.map(s => ({
      key: s.json.key,
      url: `https://tekcent.atlassian.net/browse/${s.json.key}`
    }))
  },
  nextSteps: [
    'Review created epics and stories in Jira',
    'Validate generated documentation',
    'Proceed with sprint planning',
    'Trigger WF-2 when development begins'
  ],
  processingTime: {
    start: $('Generate TraceID').first().json.startTime || timestamp,
    end: timestamp
  }
};

return response;
